import React from "react";
import "./css/FrequentQuestions.css";
import { questionaire } from "../Data";
import { IoIosArrowUp } from "react-icons/io";

const FrequentQuestions = () => {
  const response = document.querySelector(".response__container");
  const arrow__up = document.querySelector(".arrow_up_io");
  const showResponse = (e, id) => {
    // response.classList.toggle("showText");
    // arrow__up.classList.toggle("active");
    console.log(id);
    // e.preventDefault();
  };
  return (
    <div className="frequent__container">
      <div className="question__container">
        <h1>Any Question?</h1>
      </div>
      <div className="queries__container">
        {questionaire.map(({ id, question, response }) => (
          <div className="questionaire__container">
            {question}{" "}
            <IoIosArrowUp className="arrow_up_io" onClick={showResponse(id)} />
            <div className="response__container">{response}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FrequentQuestions;
