import React, { useRef, useEffect, useState } from "react";
import "./css/ContactUs.css";
import mapboxgl from "mapbox-gl";
mapboxgl.accessToken =
  "pk.eyJ1IjoiY2hyaXNhZGJyIiwiYSI6ImNrd3QweHM4ZzBkc2YydG95aDZkb3N5djEifQ.7TrHf2HZcWAhfbP_GipNtQ";
const ContactUs = () => {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [lng, setLng] = useState(39.1663);
  const [lat, setLat] = useState(-6.8038);
  const [zoom, setZoom] = useState(14);
  //
  useEffect(() => {
    if (map.current) return; // initialize map only once
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: "mapbox://styles/mapbox/streets-v11",
      center: [lng, lat],
      zoom: zoom,
    });
  });

  useEffect(() => {
    if (!map.current) return; // wait for map to initialize
    map.current.on("move", () => {
      setLng(map.current.getCenter().lng.toFixed(4));
      setLat(map.current.getCenter().lat.toFixed(4));
      setZoom(map.current.getZoom().toFixed(2));
    });
  });
  //
  return (
    <div className="contact__us">
      <div className="contact__form__container"></div>
      <div className="contact__map__container">
        <div ref={mapContainer} className="map-container" />
      </div>
    </div>
  );
};

export default ContactUs;
