import React, { useState, useRef } from "react";
import "./css/ServiceBanner.css";
// import backgroundVideo from "../Images/service_banner_bcg.mp4";
import backgroundBcg from "../Images/service_bcg_banner.mp4";
import { RiPauseCircleLine } from "react-icons/ri";
import { RiPlayCircleLine } from "react-icons/ri";

const ServiceBanner = () => {
  const [play, playVideo] = useState(false);
  const video = document.querySelector(".serviceBannerMp4");
  // const videoControls = document.querySelector(".video__control__container");
  // const [pause, puseVideo] = useState(false);
  const videoRef = useRef();
  return (
    <div className="serviceBanner">
      <video autoPlay loop muted className="serviceBannerMp4" useRef={videoRef}>
        <source src={backgroundBcg} type="video/mp4" />
      </video>
      <div
        className="video__control__container"
        onClick={() => playVideo(true)}
      >
        {play ? (
          <RiPauseCircleLine
            className="video__controls"
            onClick={() => this.pauseVideo}
          />
        ) : (
          <RiPlayCircleLine
            className="video__controls"
            onClick={() => this.refs.videoRef.play()}
          />
        )}
      </div>
      <div className="info__container">
        <span className="info__header">EXPLORE OUR SERVICES</span>
      </div>
    </div>
  );
};

export default ServiceBanner;
